<span class="line">  <span class="token keyword">while</span><span class="token punctuation">(</span>sol<span class="token operator">!=</span><span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">{</span></span>
<span class="line">    <span class="token keyword">if</span><span class="token punctuation">(</span>col<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token operator">!=</span>INT_MAX <span class="token operator">&amp;&amp;</span> <span class="token function">abs</span><span class="token punctuation">(</span>col<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token operator">+</span>dif_col<span class="token punctuation">)</span><span class="token operator">==</span>curr_col<span class="token punctuation">)</span><span class="token punctuation">{</span></span>
<span class="line">      sol<span class="token operator">-=</span>curr_col<span class="token punctuation">;</span></span>
<span class="line">      <span class="token keyword">if</span><span class="token punctuation">(</span>col<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token operator">+</span>dif_col<span class="token operator">&lt;</span><span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">{</span></span>
<span class="line">        dif_righe<span class="token operator">++</span><span class="token punctuation">;</span></span>
<span class="line">      <span class="token punctuation">}</span></span>
<span class="line">      <span class="token keyword">else</span> dif_righe<span class="token operator">--</span><span class="token punctuation">;</span></span>
<span class="line">      curr_righe<span class="token operator">--</span><span class="token punctuation">;</span></span>
<span class="line">      col<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">=</span> INT_MAX<span class="token punctuation">;</span></span>
<span class="line">      <span class="token function">sort</span><span class="token punctuation">(</span>righe<span class="token punctuation">.</span><span class="token function">begin</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">,</span> righe<span class="token punctuation">.</span><span class="token function">end</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token punctuation">[</span><span class="token operator">&amp;</span><span class="token punctuation">]</span><span class="token punctuation">(</span><span class="token keyword">int</span> a<span class="token punctuation">,</span> <span class="token keyword">int</span> b<span class="token punctuation">)</span><span class="token punctuation">{</span></span>
<span class="line">        <span class="token keyword">if</span><span class="token punctuation">(</span>a<span class="token operator">==</span>INT_MAX<span class="token punctuation">)</span> <span class="token keyword">return</span> <span class="token boolean">false</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token keyword">if</span><span class="token punctuation">(</span>b<span class="token operator">==</span>INT_MAX<span class="token punctuation">)</span> <span class="token keyword">return</span> <span class="token boolean">true</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token keyword">return</span> <span class="token function">abs</span><span class="token punctuation">(</span>a<span class="token operator">+</span>dif_righe<span class="token punctuation">)</span><span class="token operator">></span><span class="token function">abs</span><span class="token punctuation">(</span>b<span class="token operator">+</span>dif_righe<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
