<span class="line">      <span class="token keyword">if</span><span class="token punctuation">(</span>righe<span class="token punctuation">[</span>j<span class="token punctuation">]</span><span class="token operator">+</span>dif_righe<span class="token operator">&lt;</span><span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">{</span></span>
